import React, { Component } from 'react';
import axios from 'axios';
import DisplayImage from "./DisplayImage";
class Main extends Component {
constructor(props) {
    super(props);
    this.state = {
      image: null
    };
    this.path = this.path.bind(this);

  }
  async path(data){
    await this.setState({
        image: data
      });
  }


    render () {
        return (
<div className="container">
	<h2 id="productList" className="text-center">물품리스트</h2>
  <div className="row">
	{ this.props.products.map((product, key) => {
        	                    return (

    <div className="col-sm-3">
      <div className="card">
        <img className="card-img-top" src={product.img} alt="이미지"></img>
        <div className="card-body">
          <h5 className="card-title">#{product.id.toString()}</h5>
	  <h4 className="card-title">{product.name}</h4>
          <p className="card-text .font-weight-bold">가격 :{window.web3.utils.fromWei(product.price.toString(), 'Ether')} ETH</p>
          <p className="card-text text-right text-secondary blockquote"><small>owner: {product.owner}</small></p>
          <div className="card-footer">{ !product.purchased
                                        ?   <button type="button" className="btn btn-block btn-outline-success" 
                                                name={product.id}
                                                value={product.price}
                                                onClick={ (event) => {
                                                    this.props.purchaseProduct(event.target.name, event.target.value)
                                                } }
                                            >
                                            구매하기
                                            </button>
                                        : null
                                    }</div>
        </div>
      </div>
    </div>
	)
                        }) }

  </div>
</div>

        );
    }

}

export default Main;
