import React, { Component } from 'react';
import axios from 'axios';
class PlantData extends Component {
constructor(props) {
    super(props);
  }
componentDidMount(){
	this._sensorDB();
}

_sensorDB = async() =>{
	const res = await axios.get("http://127.0.0.1:4000/sensor");
	console.log(res.data);
	return res;
}

  render () {
          return (
<div className="PlantData">
    <div className="row">
		  <div className="col-12">
		  <hr/>
		  {this.sensorDB}
		  <h1>생장정보</h1>
	<div className="table-responsive">
         <table className="table table-striped">
	 <thead>
	   <tr>
	      <th>ID</th>
	      <th>이름</th>
	      <th>가격</th>
	      <th>사진</th>
	      <th>등록</th>
	   </tr>
	 </thead>
	 <tbody>
	   <tr>
	      <td>ex</td>
	      <td>ex</td>
	      <td>ex</td>
              <td>ex</td>
	      <td>
	          <form onSubmit={ async (event) => {
                    event.preventDefault();
                    const res = await axios.get("http://127.0.0.1:4000/sensor");
			console.log(res);
                } }>
	      <button type="submit" className="btn btn-block btn-outline-success">
                        상품등록
		 </button></form>
	      </td>
	   </tr>
	 </tbody> 
         </table>
          </div>
        </div>
    </div>
  <div className="row">
    <div className="col-12">
		  <hr/>
		  <h2>트랜잭션 리스트</h2>
		  { this.props.products.map((product,key) =>{
			return(
<div className="card card-floating-custom">
  <div className="card-header bg-light">
    #. {key}<br />
    Block Hash: <span className="ellipsis text-dark">{key}</span><br />
    Parent Hash: <span className="ellipsis text-dark">{key}</span><br />
    Hash: <span className="ellipsis">{key}</span><br />
  </div>
  <div className="card-body">
    <span className="bg-dark text-light">From: <span className="ellipsis bg-dark">{key}</span></span><br />
    <span className="bg-dark text-light">To: <span className="ellipsis bg-dark">{key}</span></span><br />

  </div>
  <div className="card-footer bg-light">
    Gas: {key}<br />
    Gas Price: {key}<br />
  </div>
</div>

			);
		 	 }
		  )}
    </div>

  </div>
</div>
);
    }
}

export default PlantData;
