import React, { Component } from 'react';
import axios from 'axios';
import DisplayImage from "./DisplayImage";
class Main extends Component {
constructor(props) {
    super(props);
    this.state = {
      image: null
    };
    this.path = this.path.bind(this);

  }
  async path(data){
    await this.setState({
        image: data
      });
  }


    render () {
        return (
            <div id="content">
                <h1>Add Product</h1>
                <form onSubmit={ async (event) => {
                    event.preventDefault();
                    const name = this.productName.value;
                    const price = window.web3.utils.toWei(this.productPrice.value.toString(), 'Ether');
		    const img = this.state.image;
		    const formData = new FormData();
		    formData.append('file',img);
		    const res = await axios.post("http://127.0.0.1:4000/api/upload", formData);
	            const imagePath =(res["data"]["imgPath"]).toString();
                    this.props.createProduct(name, price, imagePath);
                } }>

		    <div className="form-group mr-sm-2">
			<DisplayImage 
			imagePath={ this.path }/>
                    </div>

                    <div className="form-group mr-sm-2">
                        <input
                            id="productName"
                            ref={(input) => { this.productName = input }}
                            type="text"
                            className="form-control"
                            placeholder="product name"
                            required />
                    </div>

                    <div className="form-group mr-sm-2">
                        <input
                            id="productPrice"
                            type="text"
                            ref={(input) => { this.productPrice = input }}
                            className="form-control"
                            placeholder="product price (in ether)"
                            required />
                    </div>

                    <button type="submit" className="btn btn-success">
                        Add Product
                    </button>

                </form>

                <p>&nbsp;</p>
                <h2 id="productList">물품리스트</h2>
<div class="container">
  <div class="row">
	{ this.props.products.map((product, key) => {
        	                    return (

    <div class="col-6">
      <div class="card">
        <img src={product.img} alt="이미지"></img>
        <div class="card-body">
          <h5 class="card-title">#{product.id.toString()}{product.name}</h5>
          <p class="card-text">{window.web3.utils.fromWei(product.price.toString(), 'Ether')} ETH</p>
          <p class="card-text text-light">owner: {product.owner}</p>
          <a href="#" class="btn btn-primary">{ !product.purchased
                                        ?   <button
                                                name={product.id}
                                                value={product.price}
                                                onClick={ (event) => {
                                                    this.props.purchaseProduct(event.target.name, event.target.value)
                                                } }
                                            >
                                            구매하기
                                            </button>
                                        : null
                                    }</a>
        </div>
      </div>
    </div>
	)
                        }) }

  </div>
</div>


            </div>
        );
    }

}

export default Main;
