import React, { Component } from 'react';
import axios from 'axios';
import DisplayImage from "./DisplayImage";
class Settings extends Component {
constructor(props) {
    super(props);
    this.state = {
      image: null
    };
    this.path = this.path.bind(this);

  }
  async path(data){
    await this.setState({
        image: data
      });
  }

  render () {
          return (
<div className="row">
    <div className="col-6">
		  <hr/>
	<h2>상품 등록</h2>
		  <div className="card">
		  <div className="card-body">
                <form onSubmit={ async (event) => {
                    event.preventDefault();
                    const name = this.productName.value;
                    const price = window.web3.utils.toWei(this.productPrice.value.toString(), 'Ether');
		    const img = this.state.image;
		    const formData = new FormData();
		    formData.append('file',img);
		    const res = await axios.post("http://127.0.0.1:4000/api/upload", formData);
	            const imagePath =(res["data"]["imgPath"]).toString();
                    this.props.createProduct(name, price, imagePath);
                } }>

		    <div className="form-group mr-sm-2">
			<DisplayImage
			imagePath={ this.path }/>
                    </div>

                    <div className="form-group mr-sm-2">
                        <input
                            id="productName"
                            ref={(input) => { this.productName = input }}
                            type="text"
                            className="form-control"
                            placeholder="product name"
                            required />
                    </div>

                    <div className="form-group mr-sm-2">
                        <input
                            id="productPrice"
                            type="text"
                            ref={(input) => { this.productPrice = input }}
                            className="form-control"
                            placeholder="product price (in ether)"
                            required />
                    </div>
		    <div className="card-footer">
                    <button type="submit" className="btn btn-block btn-outline-success">
                        상품 등록
                    </button>
		    </div>
                </form>
	      </div>
	     </div>
     </div>
    <div className="col-6">
		  <hr/>
		  <h2>트랜잭션 리스트</h2>
		  { this.props.blocks.map((block,key) =>{
			var transaction = block.transactions;
			var tx = Object(transaction[0]);
		console.log(key);	  
			return(
<div className="card">
  <div className="card-header">
    #. {tx.blockNumber}<br />
    Parent Hash: {block.parentHash}<br />
    Hash: {tx.hash}<br />
    Block Hash: {tx.blockHash}
  </div>
  <div className="card-body">
    From: {tx.from}<br />
    To: {tx.to}
  </div>
  <div className="card-footer">
    Gas: {tx.gas}<br />
    Gas Price: {tx.gasPrice}
    Time: {block.timestamp}
  </div>
</div>			);
		 	 }
		  )}

    </div>

</div>
);
    }

}

export default Settings;
